def _on_language_changed_event(self, language_code):
    """Обработчик события изменения языка интерфейса."""
    try:
        import logging
        logger = logging.getLogger(__name__)
        logger.info(f"Получено событие смены языка на: {language_code}")

        # Обновляем заголовок окна
        from app.ui.i18n.translator import tr
        self.setWindowTitle(tr("app.title", "GopiAI"))

        # Обновляем все меню
        if hasattr(self, "menu_manager"):
            logger.info("Обновляем переводы меню")
            if hasattr(self.menu_manager, "update_translations"):
                self.menu_manager.update_translations()
            else:
                logger.warning("Метод update_translations не найден в menu_manager")

        # Обновляем заголовки вкладок
        self._update_tab_titles()

        # Обновляем все панели и доки
        for child in self.findChildren(QWidget):
            # Обновляем заголовки
            if hasattr(child, "windowTitle") and hasattr(child, "objectName"):
                obj_name = child.objectName()
                if obj_name and not obj_name.startswith("qt_"):
                    title_key = f"dock.{obj_name}"
                    translated_title = tr(title_key, child.windowTitle())
                    if translated_title != child.windowTitle():
                        child.setWindowTitle(translated_title)
                        logger.debug(f"Обновлен заголовок {obj_name}: {translated_title}")

            # Обновляем тексты кнопок
            if isinstance(child, QPushButton) and hasattr(child, "objectName"):
                obj_name = child.objectName()
                if obj_name and not obj_name.startswith("qt_"):
                    button_key = f"button.{obj_name}"
                    translated_text = tr(button_key, child.text())
                    if translated_text != child.text():
                        child.setText(translated_text)
                        logger.debug(f"Обновлен текст кнопки {obj_name}: {translated_text}")

            # Обновляем тексты меток
            if isinstance(child, QLabel) and hasattr(child, "objectName"):
                obj_name = child.objectName()
                if obj_name and not obj_name.startswith("qt_"):
                    label_key = f"label.{obj_name}"
                    translated_text = tr(label_key, child.text())
                    if translated_text != child.text():
                        child.setText(translated_text)
                        logger.debug(f"Обновлен текст метки {obj_name}: {translated_text}")

        logger.info(f"Язык успешно изменен на {language_code}")
    except Exception as e:
        import traceback
        logger.error(f"Ошибка при обновлении языка UI: {str(e)}")
        logger.error(traceback.format_exc())

def analyze_python_files(self, files):
    """Анализирует Python файлы на наличие проблем с UI."""
    if not files:
        self.signals.message.emit(tr("simple_ui_auditor.python_files_not_found", "Python файлы не найдены."))
        return

    issues_found = 0
    hardcoded_colors = {}

    for i, file_path in enumerate(files):
        if not self.is_running:
            return

        try:
            with open(file_path, 'r', encoding='utf-8', errors='ignore') as f:
                content = f.read()

            # Поиск хардкодированных цветов
            hex_colors = re.findall(r'#[0-9a-fA-F]{6}', content)
            if hex_colors:
                for color in hex_colors:
                    if color not in hardcoded_colors:
                        hardcoded_colors[color] = []
                    hardcoded_colors[color].append(file_path)
                    issues_found += 1

            # Поиск нелокализованных строк
            ui_strings = re.findall(r'(?:setText|setTitle|setWindowTitle|setToolTip)\(["\']([^"\']+)["\']\)', content)
            for string in ui_strings:
                if not re.search(r'self\.tr\(["\']' + re.escape(string) + r'["\']\)', content):
                    self.signals.message.emit(
                        tr("simple_ui_auditor.unlocalized_string", "  ⚠️ {file_path}: Нелокализованная строка: \"{string}\"")
                        .format(file_path=file_path, string=string)
                    )
                    issues_found += 1

            # Обновляем прогресс
            progress = int((i + 1) / len(files) * 100)
            self.signals.progress.emit(progress)

        except Exception as e:
            self.signals.message.emit(tr("simple_ui_auditor.analysis_error", f"  ⚠️ Ошибка при анализе {file_path}: {str(e)}"))

    # Отчет по хардкодированным цветам
    if hardcoded_colors:
        self.signals.message.emit(tr("simple_ui_auditor.hardcoded_colors_found", "\n  ⚠️ Найдено {count} различных хардкодированных цветов:").format(count=len(hardcoded_colors)))
        most_used_colors = sorted([(color, len(files)) for color, files in hardcoded_colors.items()],
                                key=lambda x: x[1], reverse=True)[:10]
        for color, count in most_used_colors:
            self.signals.message.emit(tr("simple_ui_auditor.color_usage", "    - {color}: {count} использований").format(color=color, count=count))
    else:
        self.signals.message.emit(tr("simple_ui_auditor.no_hardcoded_colors", "  ✅ Хардкодированные цвета не найдены"))

    if issues_found == 0:
        self.signals.message.emit(tr("simple_ui_auditor.no_issues_found", "  ✅ В Python файлах проблем не обнаружено"))

def _extract_improvement_areas(self, text: str) -> List[str]:
    """
    Извлекает области для улучшения из текста оценки

    Args:
        text: Текст оценки

    Returns:
        Список областей для улучшения
    """
    improvement_areas = []
    keywords = ["улучшени", "improvement", "рекомендац", "recommendation"]
    lines = text.split("\n")
    in_section = False

    for line in lines:
        line = line.strip()
        if not line:
            continue

        for keyword in keywords:
            if keyword.lower() in line.lower():
                in_section = True
                break

        if in_section and (line.startswith("-") or line.startswith("•") or
                          (line[0].isdigit() and "." in line[:3])):
            clean_line = line.lstrip("-•0123456789. ")
            if clean_line:
                improvement_areas.append(clean_line)

    if not improvement_areas:
        for keyword in keywords:
            for line in lines:
                if keyword.lower() in line.lower():
                    improvement_areas.append(line.strip())
                    break

    return improvement_areas[:5]

