async def update_editor_state(self):
    """
    Обновляет сохраненное состояние редактора кода.

    Получает информацию об открытых файлах, текущем файле и позиции курсора.
    """
    try:
        # Пытаемся получить информацию об открытых файлах
        code_control = self.tools.get_tool("code_control")
        if code_control:
            # Получаем список открытых файлов
            files_result = await code_control.execute(action="get_open_files")

            if not files_result.error:
                files_info = files_result.output
            else:
                files_info = "Не удалось получить информацию об открытых файлах"

            # Получаем текущий файл
            current_file_result = await code_control.execute(action="get_current_file")
            if not current_file_result.error:
                current_file = current_file_result.output
            else:
                current_file = "Не удалось получить текущий файл"

            # Сохраняем состояние
            self._current_editor_state = {
                "open_files": files_info,
                "current_file": current_file
            }

            return self._current_editor_state
    except Exception as e:
        logger.error(f"Ошибка при обновлении состояния редактора: {e}")

    return None

def hash_ast_node(node: ast.AST) -> str:
    """
    Создает хеш AST-узла для сравнения семантически эквивалентных функций

    Игнорирует имена переменных, значения литералов и комментарии,
    фокусируясь на структуре кода.
    """
    if isinstance(node, (ast.Name, ast.Attribute)):
        # Для имен переменных используем фиксированное значение
        return f"NAME_{type(node).__name__}"
    elif isinstance(node, ast.Constant):
        # Для констант используем тип значения
        return f"CONST_{type(node.value).__name__}"
    elif isinstance(node, ast.Str):
        # Строки в старых версиях Python
        return "CONST_str"
    elif isinstance(node, ast.Num):
        # Числа в старых версиях Python
        return "CONST_num"
    elif isinstance(node, ast.AST):
        # Для других узлов AST сохраняем структуру
        fields = []
        for field, value in ast.iter_fields(node):
            if field != 'ctx' and field != 'lineno' and field != 'col_offset' and field != 'end_lineno' and field != 'end_col_offset':
                if isinstance(value, list):
                    fields.append((field, [hash_ast_node(v) for v in value if isinstance(v, ast.AST)]))
                elif isinstance(value, ast.AST):
                    fields.append((field, hash_ast_node(value)))

        node_hash = f"{type(node).__name__}({','.join(f'{f}={v}' for f, v in fields)})"
        return hashlib.md5(node_hash.encode('utf-8')).hexdigest()
    else:
        # Для других типов (не AST) используем строковое представление
        return str(type(node).__name__)

